#![feature(try_find)]

use thiserror::Error;

fn main() -> anyhow::Result<()> {
    let floor = santas_final_floor(INPUT)?;
    println!("part 1: {floor}");

    let step = when_does_santa_get_to_the_basement(INPUT)?;
    println!("part 2: {step}");

    Ok(())
}

fn santas_final_floor(input: &str) -> Result<i32, InvalidCharacter> {
    input
        .chars()
        .map(Direction::try_from)
        .try_fold(0, |floor, maybe_direction| {
            maybe_direction.map(|direction| {
                let delta: i32 = direction.into();
                floor + delta
            })
        })
}

fn when_does_santa_get_to_the_basement(input: &str) -> Result<usize, InvalidCharacter> {
    input
        .chars()
        .map(Direction::try_from)
        .scan(Ok(0), |state: &mut Result<i32, InvalidCharacter>, maybe_direction| -> Option<Result<i32, InvalidCharacter>> {
            *state = match state {
                Ok(current_floor) => match maybe_direction {
                    Ok(direction) => {
                        let delta: i32 = direction.into();
                        Ok(*current_floor + delta)
                    },
                    Err(err) => Err(err),
                },
                Err(_) => return None,
            };

            Some(*state)
        })
        .enumerate()
        .try_find(|(_, maybe_floor)| maybe_floor.map(|floor| floor < 0))
        .map(|maybe_tuple| maybe_tuple.map(|(index, _)| index + 1).unwrap_or_default())
}

#[derive(Clone, Copy, Debug)]
enum Direction {
    Up,
    Down,
}

impl TryFrom<char> for Direction {
    type Error = InvalidCharacter;

    fn try_from(value: char) -> Result<Self, Self::Error> {
        match value {
            '(' => Ok(Direction::Up),
            ')' => Ok(Direction::Down),
            _ => Err(InvalidCharacter(value)),
        }
    }
}

impl Into<i32> for Direction {
    fn into(self) -> i32 {
        match self {
            Direction::Up => 1,
            Direction::Down => -1,
        }
    }
}

#[derive(Clone, Copy, Debug, Error, PartialEq, Eq)]
#[error("invalid character {0}")]
struct InvalidCharacter(char);

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_santas_final_floor() {
        assert_eq!(santas_final_floor("(())"), Ok(0));
        assert_eq!(santas_final_floor("()()"), Ok(0));
        assert_eq!(santas_final_floor("((("), Ok(3));
        assert_eq!(santas_final_floor("(()(()("), Ok(3));
        assert_eq!(santas_final_floor("))((((("), Ok(3));
        assert_eq!(santas_final_floor("())"), Ok(-1));
        assert_eq!(santas_final_floor("))("), Ok(-1));
        assert_eq!(santas_final_floor(")))"), Ok(-3));
        assert_eq!(santas_final_floor(")())())"), Ok(-3));
        assert_eq!(santas_final_floor("()()e)"), Err(InvalidCharacter('e')));
    }

    #[test]
    fn test_when_does_santa_get_to_the_basement() {
        assert_eq!(when_does_santa_get_to_the_basement(")"), Ok(1));
        assert_eq!(when_does_santa_get_to_the_basement("()())"), Ok(5));
        assert_eq!(when_does_santa_get_to_the_basement("()e)()"), Err(InvalidCharacter('e')));
        assert_eq!(when_does_santa_get_to_the_basement("())e()"), Ok(3));
    }
}

