#![feature(iterator_try_collect)]

use std::{collections::HashSet, iter};
use thiserror::Error;

fn main() -> anyhow::Result<()> {
    let houses_visited = solo_deliveries(INPUT)?;
    println!("part 1: {houses_visited}");

    let team_houses_visited = team_deliveries(INPUT)?;
    println!("part 2: {team_houses_visited}");

    Ok(())
}

const STARTING_POINT: (i32, i32) = (0, 0);

fn track_deliveries(
    input: impl Iterator<Item = char>,
) -> Result<HashSet<(i32, i32)>, InvalidCharacter> {
    iter::once(Ok(STARTING_POINT))
        .chain(
            input
                .map(Direction::try_from)
                .scan(Ok(STARTING_POINT), |state, maybe_direction| {
                    let Ok((x, y)) = state else { return None };

                    *state = match maybe_direction {
                        Ok(direction) => {
                            let (dx, dy) = direction.into();
                            Ok((*x + dx, *y + dy))
                        }
                        Err(err) => Err(err),
                    };

                    Some(*state)
                }),
        )
        .try_collect::<HashSet<_>>()
}

fn solo_deliveries(input: &str) -> Result<usize, InvalidCharacter> {
    Ok(track_deliveries(input.chars())?.len())
}

fn team_deliveries(input: &str) -> Result<usize, InvalidCharacter> {
    Ok(track_deliveries(input.chars().step_by(2))?
        .union(&track_deliveries(input.chars().skip(1).step_by(2))?)
        .count())
}

#[derive(Clone, Copy, Debug)]
enum Direction {
    Up,
    Right,
    Down,
    Left,
}

impl TryFrom<char> for Direction {
    type Error = InvalidCharacter;

    fn try_from(value: char) -> Result<Self, Self::Error> {
        match value {
            '^' => Ok(Direction::Up),
            '>' => Ok(Direction::Right),
            'v' => Ok(Direction::Down),
            '<' => Ok(Direction::Left),
            _ => Err(InvalidCharacter(value)),
        }
    }
}

impl Into<(i32, i32)> for Direction {
    fn into(self) -> (i32, i32) {
        match self {
            Direction::Up => (0, -1),
            Direction::Right => (1, 0),
            Direction::Down => (0, 1),
            Direction::Left => (-1, 0),
        }
    }
}

#[derive(Clone, Copy, Debug, Error, PartialEq, Eq)]
#[error("invalid character '{0}'")]
struct InvalidCharacter(char);

#[cfg(test)]
mod tests {
    use super::*;

    #[test]
    fn test_solo_santa_deliveries() {
        assert_eq!(solo_deliveries(">"), Ok(2));
        assert_eq!(solo_deliveries("^>v<"), Ok(4));
        assert_eq!(solo_deliveries("^v^v^v^v^v"), Ok(2));
    }

    #[test]
    fn test_team_santa_deliveries() {
        assert_eq!(team_deliveries("^v"), Ok(3));
        assert_eq!(team_deliveries("^>v<"), Ok(3));
        assert_eq!(team_deliveries("^v^v^v^v^v"), Ok(11));
    }
}

